css/style.css:
/*


/*
===========================
CONTENTS:

01 General
02 Typography
03 Non-semantic
04 Navbar
05 Page sections
06 Footer
07 Icons
08 Buttons
09 Media queries 
===========================
*/
/* Import css */
/* Google font */
@import url("../../../../fonts.googleapis.com/css-family=Open+Sans-300,400,600,700.css")/*tpa=http://fonts.googleapis.com/css?family=Open+Sans:300,400,600,700*/;
/* bootstrap */
@import url("bootstrap.css")/*tpa=http://iweb-studio.com/wrapbootstrap/maxim/css/bootstrap.css*/;
/* bootstrap responsive */
@import url("bootstrap-responsive.css")/*tpa=http://iweb-studio.com/wrapbootstrap/maxim/css/bootstrap-responsive.css*/;
/* prettyPhoto */
@import url("prettyPhoto.css")/*tpa=http://iweb-studio.com/wrapbootstrap/maxim/css/prettyPhoto.css*/;
/* flexslider */
@import url("flexslider.css")/*tpa=http://iweb-studio.com/wrapbootstrap/maxim/css/flexslider.css*/;

/* font awesome */
@import url("font-awesome.css")/*tpa=http://iweb-studio.com/wrapbootstrap/maxim/css/font-awesome.css*/;
/* overwrite */
@import url("overwrite.css")/*tpa=http://iweb-studio.com/wrapbootstrap/maxim/css/overwrite.css*/;
/* animation */
@import url("animate.css")/*tpa=http://iweb-studio.com/wrapbootstrap/maxim/css/animate.css*/;



/* =========================================================
01. General
============================================================ */

div, article, section, header, footer, nav, li {
	position:relative;
}
.group:after {
	display:block;
	height:0;
	clear:both;
	content:".";
	visibility:hidden; /* For clearing */
}
body {
	background:#fff;
	color: #444;
	font-size: 16px;
	font-weight:300;
	font-family:'Open Sans', Arial, sans-serif;
}

body {
    padding-top: 60px; /* 60px to make the container go all the way to the bottom of the topbar */
}

::-moz-selection {
	color:#333;
}
::selection {
	color:#333;
}

/* scroll to top */
.scrollup{
    position:fixed;
	width:32px;
	height:32px;
    bottom:20px;
    right:20px;
}

a.scrollup {
	outline:0;
	text-decoration:none;
}

a.scrollup:hover,a.scrollup:active,a.scrollup:focus {
	opacity:1;
	text-decoration:none;
	-webkit-transition: all 400ms ease-out 0s;	
	   -moz-transition: all 400ms ease-out 0s;
		 -o-transition: all 400ms ease-out 0s;
		    transition: all 400ms ease-out 0s;
}
a.scrollup i:hover {
	text-decoration:none;
}

.blankdivider10 {
	height: 10px;
	display: block;
}

.blankdivider20 {
	height: 20px;
	display: block;
}
.blankdivider30 {
	height: 30px;
	display: block;
}

.row {
	margin-bottom:30px;
}

.row .row{
	margin-bottom:30px;
}

.row.nomargin {
	margin-bottom:0;
}

.copyrights{
	text-indent:-9999px;
	height:0;
	line-height:0;
	font-size:0;
	overflow:hidden;
}
/*---------- forms ----------*/

input[type=text], input[type=submit], textarea {
	width: 100%;
	padding: 0.5em;
	font-size: 1em;
	margin-bottom: 1.5em;
	box-sizing: border-box;
	-moz-box-sizing: border-box;
	-webkit-box-sizing: border-box;
}
input, button, select, textarea {
	font-family:'Open Sans', Arial, sans-serif;
}
/* =========================================================
02. Typography
============================================================ */

p {
	line-height: 1.6em;
	margin: 0 0 10px 0;
}

blockquote {
	font-size:18px;
	border:none;
	padding:0;
}

blockquote.large {
	font-size:36px;
	font-weight:700;
	line-height:1.1em;
}
blockquote.large:before {
	font-size:64px;
	font-family: FontAwesome;
	content: "\f10d";
	margin-right:20px;
}

blockquote cite 						{ display:block; font-size:12px;}
blockquote cite:before 					{ content:"\2014 \0020"; }
blockquote cite a,
blockquote cite a:visited,
blockquote cite a:visited 				{ color:#555; }

ul, ol {
	font-size: 1em; /* 16px */
	line-height: 1.5; /* 24px */
	margin: 0 0 1.5em 0;
}
body, input, textarea {
}

a {
  color: #454545;
  text-decoration: none;
}

a:hover, a:active {
	text-decoration: underline;
}
h1, h2, h3, h4, h5, h6 {
	font-weight: 400;
	color: #312f2b;
	line-height:1.1em;
	font-family:'Open Sans', Arial, sans-serif;
}
h1 {
	font-size: 36px;
	margin-bottom:20px;
}
h2 {
	font-size: 30px;
	margin-bottom:20px;
}
